/* Dark mode styles */
.dark-mode {
  --bg-main: #283046;
  --text-main: #d0d2d6;
  --border-color: #404656;
  --sidebar-bg: #1a233a;
  --card-bg: #2b3547;
}

.dark-mode body {
  background-color: var(--bg-main);
  color: var(--text-main);
}

.dark-mode .card,
.dark-mode .navbar,
.dark-mode .sidebar {
  background-color: var(--card-bg);
  border-color: var(--border-color);
}

.dark-mode .table {
  color: var(--text-main);
}

.dark-mode .border {
  border-color: var(--border-color) !important;
}

