import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Eye, EyeOff, AlertCircle, BookOpen, PenTool } from "lucide-react";
import { useDispatch } from "react-redux";
import {login} from "../Redux/authSlice";


const LoginPage = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [showPassword, setShowPassword] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true); // Set loading to true when the request starts

    try {
      // Send a POST request to the backend login endpoint
      const response = await fetch(`${import.meta.env.VITE_API_URL}/api/users/loginUser`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: "include",
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      if (response.ok) {
        // Save the token to localStorage or context
        console.log("login data:  ", data)
        localStorage.setItem('token', data.token);


        // Dispatch the loginSuccess action with user data
        dispatch(login({ user: data.user }));

        // Redirect to the dashboard or another page
        navigate('/dashboard');
      } else {
        // Handle login errors
        setError('Invalid credentials');
      }
    } catch (error) {
      console.error('Error during login:', error);
      setError('An error occurred. Please try again.');
    } finally {
      setLoading(false); // Set loading to false when the request completes
    }
  };

  return (
    <div className="min-h-screen grid grid-cols-1 md:grid-cols-2 bg-gradient-to-br from-orange-50 to-amber-50">
      {/* Left Section - Login Form */}
      <div className="p-8 flex items-center justify-center">
        <div className="max-w-md w-full space-y-8">
          <div className="text-center">
            <div className="mb-6 inline-flex items-center gap-2 text-orange-600">
              <BookOpen size={32} />
              <span className="text-2xl font-bold">Welcome Back</span>
            </div>
            <h1 className="text-4xl font-serif font-bold text-gray-900 mb-2">
              Continue Your Journey
            </h1>
            <p className="text-gray-600">
              Sign in to access your author dashboard
            </p>
          </div>

          {error && (
            <div className="flex items-center gap-2 p-3 bg-red-100 text-red-700 rounded-lg">
              <AlertCircle size={20} />
              <span>{error}</span>
            </div>
          )}

          <form onSubmit={handleSubmit} className="space-y-6">
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Email Address
                </label>
                <input
                  type="email"
                  placeholder="author@yourpublication.com"
                  className="w-full px-4 py-3 rounded-lg border border-gray-300 focus:ring-2 focus:ring-orange-500 focus:border-transparent"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Password
                </label>
                <div className="relative">
                  <input
                    type={showPassword ? 'text' : 'password'}
                    placeholder="••••••••"
                    className="w-full px-4 py-3 rounded-lg border border-gray-300 focus:ring-2 focus:ring-orange-500 focus:border-transparent pr-12"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                  />
                  <button
                    type="button"
                    onClick={() => setShowPassword(!showPassword)}
                    className="absolute right-4 top-3.5 text-gray-400 hover:text-orange-600"
                  >
                    {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
                  </button>
                </div>
              </div>
            </div>

            <div className="flex items-center justify-between">
              <div className="flex items-center">
                <input
                  type="checkbox"
                  className="rounded border-gray-300 text-orange-600 focus:ring-orange-500"
                />
                <label className="ml-2 text-sm text-gray-600">Remember me</label>
              </div>
              <a
                href="/forgot-password"
                className="text-sm text-orange-600 hover:text-orange-700"
              >
                Forgot password?
              </a>
            </div>

            <button
              type="submit"
              className="w-full bg-orange-600 text-white py-3.5 rounded-lg font-medium hover:bg-orange-700 transition-colors flex items-center justify-center gap-2"
              disabled={loading}
            >
              {loading ? (
                <>
                  <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin" />
                  Signing in...
                </>
              ) : (
                <>
                  <LogIn size={20} />
                  Sign in to Dashboard
                </>
              )}
            </button>
          </form>

          <div className="relative">
            <div className="absolute inset-0 flex items-center">
              <div className="w-full border-t border-gray-300"></div>
            </div>
            <div className="relative flex justify-center text-sm">
              <span className="px-2 bg-transparent text-gray-500">
                Or continue with
              </span>
            </div>
          </div>

          <div className="flex gap-4 justify-center">
            <button className="p-3 rounded-lg border border-gray-300 hover:bg-orange-50 transition-colors">
              <img
                src="https://img.icons8.com/?size=100&id=17949&format=png&color=000000"
                alt="Google Scholar"
                className="w-6 h-6"
              />
            </button>
            <button className="p-3 rounded-lg border border-gray-300 hover:bg-orange-50 transition-colors">
              <img
                src="https://img.icons8.com/?size=100&id=uLWV5A9vXIPu&format=png&color=000000"
                alt="Facebook"
                className="w-6 h-6"
              />
            </button>
          </div>

          <p className="text-center text-sm text-gray-600">
            Don't have an account?{' '}
            <a
              href="/signup"
              className="font-medium text-orange-600 hover:text-orange-700 transition-colors"
            >
              Start your journey
            </a>
          </p>
        </div>
      </div>

      {/* Right Section - Showcase */}
      <div className="hidden md:flex bg-gradient-to-br from-orange-500 to-amber-600 p-12 items-center justify-center">
        <div className="max-w-md text-center text-white">
          <h2 className="text-3xl font-bold mb-6">Your Writing Journey Awaits</h2>
          <p className="text-lg mb-8">
            Access your dashboard to manage your publications, track your earnings,
            and connect with your readers.
          </p>
          <div className="grid grid-cols-2 gap-6">
            <div className="p-6 bg-white/10 rounded-lg">
              <h3 className="text-xl font-semibold mb-2">6+ Books</h3>
              <p className="text-sm opacity-90">Published Works</p>
            </div>
            <div className="p-6 bg-white/10 rounded-lg">
              <h3 className="text-xl font-semibold mb-2">15k+</h3>
              <p className="text-sm opacity-90">Active Readers</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
