@import url('https://fonts.googleapis.com/css2?family=Montserrat:wght@500;600;700&family=Raleway:wght@400;500;600&family=Merriweather:wght@400;700&display=swap');

.font-montserrat { font-family: 'Montserrat', sans-serif; }
.font-raleway { font-family: 'Raleway', sans-serif; }
.font-merriweather { font-family: 'Merriweather', serif; }

.hover-scale {
  transition: transform 0.2s ease;
}

.hover-scale:hover {
  transform: translateY(-2px);
}

.bg-soft-primary { background-color: rgba(105, 108, 255, 0.1); }
.bg-soft-warning { background-color: rgba(255, 171, 0, 0.1); }
.bg-soft-success { background-color: rgba(113, 221, 55, 0.1); }
.bg-soft-danger { background-color: rgba(255, 62, 62, 0.1); }
